
public class Sample3_17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "hello";
		
		char chr1 = str.charAt(0);
		char chr2 = str.charAt(1);
		int len = str.length();
		System.out.println(str+"第一個字元是"+chr1);
		System.out.println(str+"第二個字元是"+chr2);
		System.out.println(str+"的長度是"+len);
	}

}
